import React, { useState } from "react";

const App = () => {
  const [packingList, setPackingList] = useState([]);

  const onFormSubmit = (item) => {
    setPackingList([...packingList, item]);
  };

  return (
    <div className="app">
      <Logo />
      <Form onFormSubmit={onFormSubmit} />
      <PackingList packingList={packingList} setPackingList={setPackingList} />
      <Stats />
    </div>
  );
};

const Logo = () => {
  return <h1>üå¥ Far Away üß≥</h1>;
};

const Form = ({ onFormSubmit }) => {
  const [formValues, setFormValues] = useState({
    description: "",
    quantity: 1,
  });

  const onSubmit = (e) => {
    e.preventDefault();
    if (!formValues.description) return;

    const newItem = {
      ...formValues,
      id: Date.now(),
      packed: false,
    };
    setFormValues({ description: "", quantity: 1 });
    onFormSubmit(newItem);
  };

  const onFormChange = ({ target }, key) =>
    setFormValues((prevValue) => ({
      ...prevValue,
      [key]: target?.value,
    }));

  return (
    <form className="add-form" onSubmit={onSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        name="item"
        value={formValues.quantity}
        onChange={(e) => onFormChange(e, "quantity")}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((item) => (
          <option key={item} value={item}>
            {item}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={formValues.description}
        onChange={(e) => onFormChange(e, "description")}
      />
      <button type="submit">Add</button>
    </form>
  );
};

const PackingList = ({ packingList, setPackingList }) => {
  const onCheckboxChange = (e, item) => {
    const modifiedList = packingList.map((i) => {
      if (item.id === i.id) {
        return { ...item, packed: e.target.checked };
      }
      return i;
    });
    setPackingList(modifiedList);
  };

  const onDelete = () => { };
  return (
    <div className="list">
      {packingList.length ? (
        <ul>
          {packingList?.map((item) => (
            <Item
              onCheckboxChange={onCheckboxChange}
              key={item.id}
              item={item}
              onDelete={onDelete}
            />
          ))}
        </ul>
      ) : (
        "No items"
      )}
    </div>
  );
};

const Item = ({ item, onCheckboxChange, onDelete }) => {
  return (
    <li>
      <input
        type="checkbox"
        name="packed"
        checked={item.packed}
        onChange={(e) => onCheckboxChange(e, item)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={() => onDelete(item)}>‚ùå</button>
    </li>
  );
};
const Stats = () => {
  return (
    <footer className="stats">
      <em>üß≥ You need X items on your list, and you already packed X (X%)</em>
    </footer>
  );
};

export default App;
