import React, { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Charger", quantity: 1, packed: true },
];
const App = () => {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  )
}

const Logo = () => {
  return (
    <h1>üå¥ Far Away üß≥</h1>
  )
}

const Form = () => {
  const [formValues, setFormValues] = useState({ description: "", quantity: 1 });

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(formValues)

  }

  const onFormChange = ({ target }, key) => setFormValues(prevValue => ({
    ...prevValue,
    [key]: target?.value,
  }));

  return (
    <form className="add-form" onSubmit={onSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select name="item" value={formValues.quantity} onChange={e => onFormChange(e, "quantity")}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map(item => <option key={item} value={item}>{item}</option>)}
      </select>
      <input type="text" placeholder="Item..." value={formValues.description} onChange={e => onFormChange(e, "description")} />
      <button type="submit">Add</button>
    </form>
  )
}

const PackingList = () => {
  return (
    <div className="list">
      <ul>
        {initialItems.map(item => <Item key={item.id} item={item} />)}
      </ul>
    </div>
  )
}

const Item = ({ item }) => {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>{item.quantity} {item.description}</span>
      <button>‚ùå</button>
    </li>
  )
}
const Stats = () => {
  return (
    <footer className="stats">
      <em>üß≥ You need X items on your list, and you already packed X (X%)</em>
    </footer>
  )
}

export default App;
